INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`) VALUES ('74', '1', 'Bamboo Birth Announcement Discs', '&lt;p&gt;These bamboo announcement discs are a great way to celebrate each special birth milestone. You can use these to announce to the world the gender of your baby if you find out during pregnancy and the matching I\'m here disc when bub makes his or her appearance in the world. &lt;/p&gt;&lt;p&gt;If you want the \'I\'m here\' disc as a keepsake and not a birth announcement we can engrave each one with:&lt;/p&gt;&lt;p&gt;Name:&lt;/p&gt;&lt;p&gt;Date:&lt;/p&gt;&lt;p&gt;Time:&lt;/p&gt;&lt;p&gt;Weight:&lt;/p&gt;&lt;p&gt;Height:&lt;/p&gt;&lt;p&gt;Otherwise, these come sealed and can be written on with the details of your baby after birth.&lt;/p&gt;&lt;p style=&quot;margin-bottom: 10px; font-size: 12px;&quot;&gt;&lt;span style=&quot;font-weight: 700;&quot;&gt;&lt;font face=&quot;Arial&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&lt;br&gt;&lt;/span&gt;&lt;/font&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom: 10px; font-size: 12px;&quot;&gt;&lt;span style=&quot;font-weight: 700;&quot;&gt;&lt;font face=&quot;Arial&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;Specifications:&lt;/span&gt;&lt;/font&gt;&lt;/span&gt;&lt;/p&gt;&lt;ul style=&quot;margin-bottom: 10px; font-size: 12px;&quot;&gt;&lt;li&gt;&lt;font face=&quot;Arial&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;Australian bamboo.&lt;/span&gt;&lt;/font&gt;&lt;/li&gt;&lt;li&gt;&lt;font face=&quot;Arial&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;Each set contains one large \'I\'m Here\' disc and one boy OR girl disc.&amp;nbsp;&lt;/span&gt;&lt;/font&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p style=&quot;margin-bottom: 10px; font-size: 12px;&quot;&gt;&lt;span style=&quot;font-weight: 700;&quot;&gt;&lt;font face=&quot;Arial&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;